export default function TextForm(props) {
  const handleUpClick = ()=>{
    // console.log('Boss handleUpclick function is turned on')
    let newText = text.toUpperCase();
    setText(newText);
  }
  const handleDownClick = ()=>{
    let newText = text.toLowerCase();
    setText(newText)
  }
  
  const handleOnChange = (event) =>{
    // console.log('Changing in progress');
    // setText('')
    setText(event.target.value)

  }

  // buttons
  const onKeyPress = ()=>{
    setText2('Stopped')
  }
  const onKeyDown = ()=>{
    setText2('Writing...')
  }
  const toReset = ()=>{
    setText('')
  }
  const copy = ()=>{
    navigator.clipboard.writeText(text)
  }

  
  const [text, setText] = useState('');
  const [text2,setText2] = useState('')
  let stringifyText = JSON.parse(JSON.stringify((text.split(' '))))
  let list = []

 



const titleForm =  stringifyText.forEach(desh => {
  //   list.push(desh)
  function capitalizeFirstLetter() {
      return list.push(desh.charAt(0).toUpperCase() + desh.slice(1));
      }
  capitalizeFirstLetter()
  console.log(list)
  })
setText(list)







// console.log(text.charAt(0))
