import React,{useState} from 'react'



export default function TextForm(props) {
    const [text, setText] = useState('Enter text here');
    
    return (
        <>
    <h1>{props.heading} -{text}</h1>
    <div className="form-floating">
    <textarea className="form-control textarea" placeholder="Enter the Text" id="floatingTextarea" rows={100} cols={150}></textarea>
    {/* <label for="forMyBox">Comments</label> */}
</div><br />
<button className="btn btn-primary">convert to upper case</button>
</>
  )
}
