import React,{useState} from 'react'

export default function TextForm(props) {
  const handleUpClick = ()=>{
    // console.log('Boss handleUpclick function is turned on')
    let newText = text.toUpperCase();
    setText(newText);
  }
  const handleDownClick = ()=>{
    let newText = text.toLowerCase();
    setText(newText)
  }
  
  const handleOnChange = (event) =>{
    // console.log('Changing in progress');
    // setText('')
    setText(event.target.value)

  }

  const summary = ()=>{
    preview = document.querySelector('.preview');
    text.length>0?preview.InnerHTML= text.slice(0,280):preview.InnerHTML='Enter the text to preview'
  }

summary()
  // buttons
  const onKeyPress = ()=>{
    setText2('Stopped')
  }
  const onKeyDown = ()=>{
    setText2('Writing...')
  }
  const toReset = ()=>{
    setText('')
  }
  const copy = ()=>{
    navigator.clipboard.writeText(text)
}

  
  const [text, setText] = useState('');
  const [text2,setText2] = useState('')
  let stringifyText = JSON.parse(JSON.stringify((text.split(' '))))
  let list = []

 
  const titleForm =() =>{
  stringifyText.forEach(desh => {
  //   list.push(desh)
      return list.push(desh.charAt(0).toUpperCase() + desh.slice(1));
    })
    const test = list.join(' ')
    // console.log(test)
    setText(test)
  }
  
const extraSpaceRemover = ()=>{
  let newText = text.split(/[ ]+/)
  setText(newText.join(' '))
}

    return (
        <>
        <div className="container" style={{color: props.mode==='light'?'black':'#dcc2c2'}} >
        <link rel="stylesheet" href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@20..48,100..700,0..1,-50..200" />
    <h1>{props.heading}</h1>
    <div className='material-symbols-outlined little-Border'><div className="right-Align" onClick={copy}>
<span class="material-symbols-outlined">
content_copy
</span>
</div></div>
    <div className="form-floating" my-1>
    <textarea className="form-control textarea" value={text} style={{backgroundColor: props.mode ==='dark'?'whitesmoke':'#rgb(235 210 210)',color: 'black'}} onChange={handleOnChange} placeholder="Enter the Text" id="floatingTextarea" rows={100} cols={150} onKeyUp={onKeyPress} onKeyDown={onKeyDown} contentEditable='true'formControlName="txt" ></textarea>
    {/* <label for="forMyBox">Comments</label> */}
    </div><br />
<div className='flexKaro'>{text2}
<div className="container my3 rightAlign">
  <p>{0.0032*(text.split(' ').length - 1)} Minutes to read</p>
  <p>{text.split(' ').length - 1} Words and {text.length} Characters</p>
</div>
</div><br />
<button className="btn btn-primary" onClick={handleUpClick}>convert to upper case</button>
<button className="btn btn-primary" id='side-btn' onClick={handleDownClick}>convert to lowecase</button>
<button className="btn btn-primary" id='side-btn' onClick={toReset}>Clear Text Area</button>
<button className="btn btn-primary" id='side-btn' onClick={titleForm}>Title form</button>
<button className="btn btn-primary" id='side-btn' onClick={extraSpaceRemover}>Extra Space Remover</button>
</div>

<h2>Preview</h2>
<p class='preview'></p>
</>
  )
}
